import React, { useState, useEffect } from "react";
import "./App.css";
import SearchPanel from "./Components/SearchPanel";
import { Routes, Route, NavLink } from "react-router-dom";
import { FaCog, FaBook, FaFolderOpen, FaThLarge, FaDownload } from "react-icons/fa";

const MANUALS_LINK = [
  {
    name: "Sahara App User Manual",
    url: "https://rAyO-01.github.io/sahara-files/manuals/Sahara%20App%20User%20Manual%20-5.pdf",
  },
  {
    name:"SaharaAdmin Portal User Manual",
    url:"https://rAyO-01.github.io/sahara-files/manuals/SaharaAdminPortal%20User%20Manual.pdf",
  },
  {
    name:"TATS User Manual",
    url:"https://rAyO-01.github.io/sahara-files/manuals/TATS%20User%20Manual.pdf",
  }
];


const APPVERSIONS_LINK ={}

function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const savedMode = localStorage.getItem("darkMode");
    if (savedMode === "true") setDarkMode(true);
  }, []);

  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  return (
      <div className={`container ${darkMode ? "dark" : ""}`}>
        {/* HEADER */}
        <header className="header">
          <div className="header-title">Dashboard</div>
          <div className="header-search">
            <SearchPanel />
          </div>
          <div className="header-actions">
            <button
              className="darkMode-toggle"
              onClick={() => setDarkMode(!darkMode)}
            >
              {darkMode ? "Light" : "Dark"}
            </button>
          </div>
        </header>

        {/* SIDEBAR */}
        <div className="sidebar">
          <div className="menu-header">MENU</div>
          <div className="menu-top">
            <NavLink
              to="/manuals"
              className={({ isActive }) =>
                `menu-item ${isActive ? "active" : ""}`
              }
              aria-label="Manuals"
            >
              <FaBook className="menu-icon" aria-hidden="true" />
              <span className="menu-text">Manuals</span>
            </NavLink>

            <NavLink
              to="/versions"
              className={({ isActive }) =>
                `menu-item ${isActive ? "active" : ""}`
              }
              aria-label="App Versions"
            >
              <FaFolderOpen className="menu-icon" aria-hidden="true"/>
              <span className="menu-text">App Versions</span>
            </NavLink>

            <NavLink
              to="/section3"
              className={({ isActive }) =>
                `menu-item ${isActive ? "active" : ""}`
              }
              aria-label="Section 3"
            >
              <FaThLarge className="menu-icon" aria-hidden="true"/>
              <span className="menu-text">Section 3</span>
            </NavLink>
          </div>

          <div className="menu-bottom">
            <NavLink
              to="/settings"
              className={({ isActive }) =>
                `menu-item ${isActive ? "active" : ""}`
              }
              aria-label="Settings"
            >
              <FaCog className="menu-icon" aria-hidden="true"/>
              <span className="menu-text">Settings</span>
            </NavLink>
          </div>
        </div>

        {/* MAIN CONTENT */}
        <div className="main">
          <div className="content">
            <Routes>
              <Route path="/manuals" element={<Manuals />} />
              <Route path="/versions" element={<AppVersions />} />
              <Route path="/section3" element={<Section3 />} />
              <Route path="/settings" element={<Settings />} />
              {/* Default route */}
              <Route path="*" element={<Manuals />} />
            </Routes>
          </div>
        </div>
      </div>
  );
}

// ðŸ“‚ Manuals section
function Manuals() {
  return(
    <div>
      <h2>Manuals</h2>
      {MANUALS_LINK.map((manual, index) => (
        <div key={index} style={{ marginBottom: "40px"}}>
          <h3>{manual.name}</h3>
          <iframe
            src={manual.url}
            width="100%"
            height="500px"
            style={{border: "1px solid #ccc", borderRadius: "8px"}}
            title={manual.name}
          ></iframe>
          <div style={{marginTop: "10px"}}>
            <a
              href={manual.url} download>
                <button className="download-btn">
                  <FaDownload className="download-icon" /> Download {manual.name}
             </button>
            </a>
          </div>
        </div>  
      ))}
    </div>
  );
}

// ðŸ“‚ App Versions section
function AppVersions() {
  return (
    <div>
      <h2>App Versions</h2>
      <p>No app versions yet.</p>
    </div>  
  );
}

// ðŸ“‚ Section 3 placeholder
function Section3() {
  return <div>Section 3</div>;
}

// ðŸ“‚ Settings placeholder
function Settings() {
  return <div>Settings</div>;
}

export default App;
